    <!--[if lt IE 9]><script src="//html5shiv.googlecode.com/svn/trunk/html5.js"></script><![endif]-->
    <link href="//fonts.googleapis.com/css?family=Lato:300|Imprima" rel="stylesheet" type="text/css" />
    <style>
      html {
        margin: 0;
        padding: 0;
        min-height: 100%;
        font-family: Lato, "Helvetica Neue", Helvetica, Arial, sans-serif;
        text-align: center;
        box-shadow: inset 0 0 100px rgba(0, 0, 0, .5);
        background: #efefef url(data:image/png;base64,);
        cursor: pointer;
      }
      .container {
        width: 700px;
        margin: 0 auto;
        margin-top: 100px;
      }
      h1 {
        font-family: Imprima, "Open Sans", "Helvetica Neue", Helvetica, Arial, sans-serif;
        font-weight: 400;
        font-size: 4em;
        color: #888;
      }
      .fsc-blue {
        color: #29A1C1;
      }
      .fsc-green {
        color: #009D90;
      }
      .fsc-orange {
        color: #E45238;
      }
      .bezannes {
        color: #555555;
      }
      p {
        margin-bottom: 0;
        font-size: 2em;
        color: #666;
      }
    </style>
  </head>

	<body>

    <div class="container">
      <img src="fsc.png" alt="FSC Bezannes" />
      <h1><span class="fsc-blue">Foyer</span> <span class="fsc-green">Social</span> et <span class="fsc-orange">Culturel</span><br />de <span class="bezannes">Bezannes</span></h1>
      <p>À très vite !</p>
    </div>
    
    <script src="//ajax.googleapis.com/ajax/libs/jquery/1.9.1/jquery.min.js"></script>
    <script>
    $(function() {
      jQuery.fn.center = function () {
        this.css("position","absolute");
        this.css("top", Math.max(0, (($(window).height() - this.outerHeight()) / 2) +  $(window).scrollTop()) + "px");
        this.css("left", Math.max(0, (($(window).width() - this.outerWidth()) / 2) + $(window).scrollLeft()) + "px");
        return this;
      }
      $(".container").css("margin-top",0).center();
      $('html').click(function() {
        //window.open('http://bezannes.fr/association/foyer-social-et-culturel-10');
        document.location = 'http://bezannes.fr/association/foyer-social-et-culturel-10';
      });
    });
    </script>
